import React from 'react';

import './sign-in.styles.scss';

class SignIn extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            email: '',
            password: ''
        }
    }
    onHandle = event => {
        event.preventdefault();
        this.setState({
            email: '',
            password: ''
        })
    }
    clickHandle = event => {
        const {name, value} = event.target;
        this.setState({ [name]: value});
    }

    render(){
        return (
            <div className="sign-in">
                <h1>Please Sign Up</h1>
                <span>If your have email and password, Please Sign In</span>

                <form onSubmit={this.onHandle}>
                    <input type="email" onClick={this.clickHandle}  name="email" value={this.state.email} />
                    <label>email</label>
                    <input type="password" onClick={this.clickHandle} name="password" value={this.state.password} />
                    <label>password</label>

                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}

export default SignIn;
