export const addItemToCart = (cartItems, cardItemToAdd) => {
    const existingCarItem = cartItems.find(cartItem => cartItem.id === cardItemToAdd.id);

    if(existingCarItem){
        cartItems.map(cartItem => cartItem.id === cardItemToAdd.id ? {...cartItem, quantity: cartItem.quantity + 1} : cartItem)
    }

    return [...cartItems, {...cardItemToAdd, quantity: 1}]
};
