import { createSelector } from 'reselect';


const COLLECTION_ITEM_MAP = {
    hats: 1,
    sneakers: 2,
    jackets: 3,
    women: 4,
    men: 5
};


const selectShop = state => state.shop;

export const selectCollections = createSelector(
    [selectShop],
    shop => shop.collections
);

export const selectColletionItem = collectionParamsUrl => {
    createSelector(
        [selectCollections],
        collections => collections.find(collection => collection.id == COLLECTION_ITEM_MAP[collectionParamsUrl]) 
    )
}
