import shopActionTypes from './shop.types';

import { firestore, covertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';

export const fetchCollectionsStart = () => ({
    type: shopActionTypes.FETCH_COLLECTIONS_START
})
export const fetchCollectionsSuccess = (collectionsMap) =>({ 
    type: shopActionTypes.FETCH_COLLECTIONS_SUCCESS,
    payload: collectionsMap 
})

export const fetchCollectionsFaliur = errorMessage => ({
    type: shopActionTypes.FETCH_COLLECTIONS_FAILUR,
    payload: errorMessage
})

export const fetchCollectionsStartAysc = function(){
    return dispatch => {
        const collectionRef = firestore.collection('collections');
        dispatch(fetchCollectionsStart());

    collectionRef.get().then(snapshot => {
      const collectionsMap = covertCollectionsSnapshotToMap(snapshot)
        dispatch(fetchCollectionsSuccess(collectionsMap));
    }).catch(error => dispatch(fetchCollectionsFaliur(error.message)));
    }
}
